import React, { useEffect, useState } from "react";
import Cart from "./Cart";
import axios from "axios"
const Home = () => {
    const [list, setList] = useState([]);
    const [cart, setCart] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const food = await axios.get("http://localhost:5000/getFoodData");
                setList(food.data);
            }
            catch (e) {
                console.log(e);
            }
        }

        fetchData();

    }, [])

    // useEffect(() => {
    //     console.log(cart);
    // }, [cart]); // Log cart whenever it changes

    const addToCart = async (name, price, id) => {
        //console.log(name + " " + price + " " + id);
        const newItem = {
            name: name,
            price: price,
            id: id
        }
        setCart([...cart, newItem]);
    }

    const orderFood = async () => {
        await axios.post("http://localhost:5000/cart", { cartItems: cart });
    };

    if (list.length === 0)
        return (<>No food available</>)
    return (<>

        {list.map((e, index) =>
            <div className="container-fluid" key={e._id}>
                <div className="row">
                    <div className="col-2 m-2">
                        {e.name}
                    </div>
                    <div className="col-1 m-2">
                        {e.price}
                    </div>
                    <div className="col-2 m-2">
                        <button className="btn btn-primary" onClick={() => addToCart(e.name, e.price, e._id)}>Add</button>
                    </div>
                </div>
            </div>
        )}


        <button onClick={orderFood}>Order</button>


        <Cart cart={cart}></Cart>
    </>)
};

export default Home;
